export const POST = async (req: Request) => {
  const body = (await req.json()) as CardSkillUpdatePayload;
  const { CardSkills, deletedCardSkills } = body;

  //Generated by GenerateUniquenessValidation
  //Validate record uniqueness

  UNIQUE_FIELDS.forEach((item) => {
    const uniquenessError = checkDuplicateCombinations(item, CardSkills);

    if (uniquenessError) {
      return returnJSONResponse({
        status: "error",
        error: uniquenessError,
        errorCode: 422,
      });
    }
  });

  const t = await sequelize.transaction();
  let recordsCreated = 0;
  let recordsDeleted = 0;

  for (const item of CardSkills) {
    const validationMessage = validateFieldIfBlank(item, REQUIRED_FIELDS);
    if (validationMessage) {
      throw new Error(validationMessage);
    }
  }

  try {
    for (const item of CardSkills) {
      if (item[PRIMARY_KEY] === "") {
        await CardSkill.create(
          {
            //Generated by GetAllFieldsToUpdateBySeqModel
name: item.name!,description: item.description!
          },
          { transaction: t }
        );

        recordsCreated++;
      } else {
        await CardSkill.update(
          {
            //Generated by GetAllFieldsToUpdateBySeqModel
name: item.name!,description: item.description!
          },
          { where: { [PRIMARY_KEY]: item[PRIMARY_KEY] }, transaction: t }
        );
      }
    }

    if (deletedCardSkills.length > 0) {
      // Get the count of records to be deleted
      const recordsToDeleteCount = await CardSkill.count({
        where: { [PRIMARY_KEY]: { [Op.in]: deletedCardSkills } },
      });

      await CardSkill.destroy({
        where: { [PRIMARY_KEY]: { [Op.in]: deletedCardSkills } },
        transaction: t,
      });

      // Set the recordsDeleted count to the actual number of records deleted
      recordsDeleted = recordsToDeleteCount;
    }

    await t.commit();

    return NextResponse.json({
      recordsCreated,
      recordsDeleted,
    });
  } catch (err) {
    await t.rollback();
    return handleSequelizeError(err);
  }
};
