import sequelize from "@/config/db";
import { createTag, updateTag } from "@/utils/api/TagLibs";
import { TagUpdatePayload } from "@/interfaces/TagInterfaces";
import { TagSchema } from "@/schema/TagSchema";
import { PRIMARY_KEY, UNIQUE_FIELDS } from "@/utils/constants/TagConstants";
import handleSequelizeError from "@/utils/errorHandling";
import { checkDuplicateCombinations, returnJSONResponse } from "@/utils/utils";
import { NextResponse } from "next/server";

//Generated by GetMultiCreateModelPOSTRoute - GetMultiCreateModelPOSTRoute
export const POST = async (req: Request) => {
  const body = (await req.json()) as TagUpdatePayload;
  const { Tags } = body;

  //Generated by GenerateUniquenessValidation
  //Validate record uniqueness

  UNIQUE_FIELDS.forEach((item) => {
    const uniquenessError = checkDuplicateCombinations(item, Tags);

    if (uniquenessError) {
      return returnJSONResponse({
        status: "error",
        error: uniquenessError,
        errorCode: 422,
      });
    }
  });

  const t = await sequelize.transaction();
  let recordsCreated = 0;

  for (const item of Tags) {
    try {
      await TagSchema.validate(item);
    } catch (error) {
      return handleSequelizeError(error);
    }
  }

  try {
    for (const item of Tags) {
      if (item[PRIMARY_KEY] === "") {
        await createTag(item, t);
        recordsCreated++;
      } else {
        await updateTag(item, PRIMARY_KEY, t);
      }
    }

    await t.commit();

    return NextResponse.json({
      recordsCreated,
    });
  } catch (err) {
    await t.rollback();
    return handleSequelizeError(err);
  }
};;
