"use client";
import { GetTaskCategoriesResponse } from "@/interfaces/TaskCategoryInterfaces";
import { TaskCategorySearchParams } from "@/interfaces/TaskCategoryInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getTaskCategories = async (useName: boolean = false) => {
  const { data } = await axiosClient.get<GetTaskCategoriesResponse>(
    `task-categories`,
    {
      params: {
        fetchCount: "false",
        simpleOnly: "true",
      } as Partial<TaskCategorySearchParams>,
    }
  );

  return data.rows.map((item) => ({
    ...item,
    id: !useName ? item.id : item.name,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
  useName?: boolean;
}

const useTaskCategoryList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["taskCategory-list"],
    queryFn: () => getTaskCategories(prop?.useName),
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useTaskCategoryList;
